mapper.insert:
insert
  into academic_status
  (
    as_no
  , stud_no
  , as_type
  , off_type
  , reason
  , reason_detail
  , off_year
  , off_semester
  , offuntil_year
  , offuntil_semester
  )
  values
  (
    seq_acsno.nextval
  , #{studNo}
  , #{asType}
  , #{offType}
  , #{reason}
  , #{reasonDetail}
  , #{offYear}
  , #{offSemester}
  , #{offUntilYear}
  , #{offUntilSemester}
  )

insert
  into academic_status
  (
    as_no
  , stud_no
  , as_type
  , back_year
  , back_semester
  )
  values
  (
    seq_acsno.nextval
  , #{studNo}
  , #{asType}
  , #{backYear}
  , #{backSemester}
  )
