mapper.insert:
insert
  into attendance
  (
    attendance_no
  , class_no
  , stud_no
  , attendance_date
  , attendance_status
  , class_title
  )
  values
  (
    seq_attendance.nextval
  , #{classNo}
  , #{studNo}
  , #{attendanceDateA}
  , null
  , #{classTitle}
  )

mapper.update:
update
       attendance
   set attendance_status = #{attendanceStatus}
 where attendance_date = #{attendanceDateB}
   and class_no = #{classNo}
   and stud_no = #{studNo}

