mapper.update:
update
	   mail_from
   set title = #{title}
     , content = #{content}
     , send_date = sysdate
     , save_status = 'N'
 where mail_from_no = #{mailNo}
   and from_user = #{userNo}

mapper.insert:
insert
  into mail_from
  (
    mail_from_no
  , from_user
  , from_add
  , to_user
  , title
  , content
  )
  values
  (
    seq_mfno.nextval
  , #{userNo}
  , #{userNo} || '@unisystem.com'
  , #{userToNo}
  , #{title}
  , #{content}
  )

insert
  into attachment
  (
  	attach_no
  , ref_type
  , ref_no
  , origin_name
  , change_name
  , path
  )
  values
  (
  	seq_attno.nextval
  , 'M'
  , #{refNo}
  , #{originName}
  , #{changeName}
  , #{path}
  )
