mapper.select:
		SELECT
			   COUNT(*)
		  FROM PROFESSOR
		  JOIN USERS ON(PROF_NO = USER_NO)


		 	 	WHERE KOR_NAME LIKE '%'||#{keyword}||'%'


		 		WHERE PROF_UNIV = #{univ}


			 	WHERE PROF_UNIV = #{univ}
		  	      AND PROF_DEPARTMENT = #{depart} 


		 		WHERE PROF_UNIV = #{univ}
   		   	      AND PROF_DEPARTMENT = #{depart}  
   	     	      AND KOR_NAME LIKE '%'||#{keyword}||'%'


		   ORDER
		     BY KOR_NAME

SELECT PROF_NO , KOR_NAME , PROF_UNIV , PROF_DEPARTMENT , PROF_MAJOR , PROF_RANK , EMAIL , PHONE FROM PROFESSOR JOIN USERS ON(PROF_NO = USER_NO)
WHERE KOR_NAME LIKE '%'||#{keyword}||'%'
WHERE PROF_UNIV = #{univ}
WHERE PROF_UNIV = #{univ} AND PROF_DEPARTMENT = #{depart}
WHERE PROF_UNIV = #{univ} AND PROF_DEPARTMENT = #{depart} AND KOR_NAME LIKE '%'||#{keyword}||'%'
ORDER BY KOR_NAME

mapper.update:
UPDATE
       STUDENT
   SET PROF_NO = #{profNo}
 WHERE STUD_NO = #{studNo}

mapper.insert:
INSERT 
  INTO USERS 
     ( 
       USER_NO
     , PASSWORD
     , KOR_NAME
     , ENG_NAME
     , BIRTHDAY
     , GENDER
     , EMAIL
     , PHONE
     , ADDRESS
     , NATION
     , WEBMAIL
     , USER_DIV
     , PROFILE_IMG
     )
       VALUES
     (
       (TO_CHAR(SYSDATE, 'YYYY')) || SEQ_PROFESSOR.NEXTVAL
     , #{birthday}
     , #{korName}   
     , #{engName}
     , #{birthday}
     , #{gender}
     , #{email}
     , #{phone}
     , #{address}
     , #{nation}
     , ((TO_CHAR(SYSDATE, 'YYYY')) || SEQ_PROFESSOR.CURRVAL) || '@unisystem.com'
     , 1
     , #{profileImg}
     )

    INSERT 
	  INTO PROFESSOR 
	     ( 
	       PROF_NO
	     , PROF_RANK
		 , PROF_UNIV
	 	 , PROF_DEPARTMENT
		 , PROF_MAJOR
		 , PROF_INTO
		 , PROF_STATUS
	     )
	       VALUES
	     (
	       (TO_CHAR(SYSDATE, 'YYYY')) || SEQ_PROFESSOR.CURRVAL
	     , #{profRank}
	     , #{profUniv}
	     , #{profDepartment}
	     , #{profMajor}
	     , #{profInto}
	     , 'Y'
	     )
