mapper.select:
		SELECT
			   COUNT(*)
		  FROM STUDENT
		  JOIN USERS ON(STUD_NO = USER_NO)


	  	  WHERE STUD_UNIV = #{univ}
	  	    AND STUD_DEPARTMENT = #{depart}  



  		    WHERE KOR_NAME LIKE '%'||#{keyword}||'%'



		  WHERE STUD_UNIV = #{univ}
   		    AND STUD_DEPARTMENT = #{depart}  
   	     	AND KOR_NAME LIKE '%'||#{keyword}||'%'

SELECT STUD_NO , KOR_NAME , STUD_UNIV , STUD_DEPARTMENT , STUD_MAJOR , STUD_STATUS FROM STUDENT JOIN USERS ON(STUD_NO = USER_NO)
WHERE STUD_UNIV = #{univ}
WHERE STUD_UNIV = #{univ} AND STUD_DEPARTMENT = #{depart}
WHERE KOR_NAME LIKE '%'||#{keyword}||'%'
WHERE STUD_UNIV = #{univ} AND STUD_DEPARTMENT = #{depart} AND KOR_NAME LIKE '%'||#{keyword}||'%'
ORDER BY KOR_NAME

mapper.insert:
INSERT 
  INTO USERS 
     ( 
       USER_NO
     , PASSWORD
     , KOR_NAME
     , ENG_NAME
     , BIRTHDAY
     , GENDER
     , EMAIL
     , PHONE
     , ADDRESS
     , NATION
     , WEBMAIL
     , USER_DIV
     , PROFILE_IMG
     )
       VALUES
     (
       (TO_CHAR(SYSDATE, 'YYYY')) || SEQ_STUDENT.NEXTVAL
     , #{birthday}
     , #{korName}   
     , #{engName}
     , #{birthday}
     , #{gender}
     , #{email}
     , #{phone}
     , #{address}
     , #{nation}
     , ((TO_CHAR(SYSDATE, 'YYYY')) || SEQ_STUDENT.CURRVAL) || '@unisystem.com'
     , 1
     , #{profileImg}
     )
